import { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [jobListings, setJobListings] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [searchLocation, setSearchLocation] = useState("");

  // Fetch job listings from API


  const filteredJobs = jobListings.filter((job) =>
    job.location.toLowerCase().includes(searchLocation.toLowerCase())
  );

  return (
    <div className="flex h-screen p-6 bg-gray-100">
      {/* Left Sidebar - Job Listings */}
      <div className="w-1/3 bg-white p-4 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4 text-blue-700">Job Listings</h2>

        {/* Search Box */}
        <input
          type="text"
          placeholder="Search by location..."
          className="w-full p-3 mb-4 border rounded-lg text-gray-700"
          value={searchLocation}
          onChange={(e) => setSearchLocation(e.target.value)}
        />

        {/* Job List */}
        <div>
          {filteredJobs.length > 0 ? (
            filteredJobs.map((job) => (
              <div
                key={job.id}
                className="p-4 mb-3 border rounded-lg cursor-pointer hover:bg-blue-100 transition"
                onClick={() => setSelectedJob(job.details)}
              >
                <h3 className="text-lg font-bold text-blue-900">{job.title}</h3>
                <p className="text-gray-800 font-medium">{job.company} - {job.location}</p>
                <p className="text-gray-700">{job.salary}</p>
                <p className="text-gray-600 text-sm mt-1">{job.description}</p>
              </div>
            ))
          ) : (
            <p className="text-gray-600">No jobs found for this location.</p>
          )}
        </div>
      </div>

      {/* Right Section - Job Details */}
      {selectedJob && (
        <div className="w-2/3 ml-6 bg-white p-8 rounded-lg shadow-lg">
          <h2 className="text-3xl font-bold text-blue-900">{selectedJob.jobTitle}</h2>
          <p className="text-lg font-semibold text-gray-700">{selectedJob.company}</p>
          <p className="text-yellow-500 text-lg font-bold mt-1">⭐ {selectedJob.rating}</p>
          <p className="text-gray-600 text-md mt-2">
            <strong>Location:</strong> {selectedJob.location}
          </p>
          <p className="text-gray-500 mt-1"><strong>Posted:</strong> {selectedJob.postedTime}</p>

          {/* Quick Apply Button */}
          <button className="mt-4 bg-pink-500 text-white py-2 px-6 rounded-lg hover:bg-pink-600 transition">
            Quick Apply
          </button>

          {/* Job Details Section */}
          <div className="mt-6">
            <h3 className="text-xl font-semibold text-gray-800">Qualifications:</h3>
            <ul className="list-disc list-inside text-gray-700 mt-2">
              {selectedJob.qualifications.map((q, index) => (
                <li key={index}>{q}</li>
              ))}
            </ul>
          </div>

          {/* Full Job Description */}
          <div className="mt-6">
            <h3 className="text-xl font-semibold text-gray-800">Job Description:</h3>
            <p className="text-gray-700 mt-2 leading-relaxed">{selectedJob.fullDescription}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
