import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import React Router for navigation
import "./style.css";

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate(); // React Router's navigation function

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (!username || !password) {
            setError("Please fill in both username and password.");
            return;
        }

        try {
            const response = await fetch("http://localhost:3000/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ username, password }),
            });

            const data = await response.json();
            if (data.success) {
                navigate("/UploadPage"); // Navigate to the Upload page
            } else {
                setError(`No account exits.Signup below!`);
            }
        } catch (error) {
            console.error("Error logging in:", error);
            setError("Something went wrong. Please try again.");
        }
    };

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit}>
                <h2>Login</h2>
                {error && <p className="error-message">{error}</p>}
                
                <div className="input-container">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>

                <div className="input-container">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>

                <button type="submit">Login</button>
                <p>Don't have an account? <a onClick={() => navigate("/SignupPage")} className="link">Sign up</a></p>
            </form>
        </div>
    );
};

export default Login;
