import { useState } from "react";
import { FaMapMarkerAlt, FaClock, FaStar } from "react-icons/fa";
import "./App.css";

function App() {
  const jobListings = [
    {
      id: 1,
      title: "Immediate Cleaning Position Available - Starting at $20.00/Hour",
      company: "Reliance Contractors",
      location: "Sandy, OR",
      rating: "★ 3.6",
      salary: "$21 - $23 an hour",
      description:
        "Clean and sanitize work areas, break rooms, and other designated areas. Identify management of any damages or repairs needed.",
      details: {
        jobTitle: "Garbage Collector (Rider)",
        company: "City Waste Management",
        rating: "★ 4.2",
        location: "Portland, OR",
        postedTime: "5 hours ago",
        qualifications: ["Entry level", "Sanitation", "Reliability"],
        fullDescription:
          "Seeking a garbage collector to handle waste collection routes in Portland. Must be reliable, willing to work outdoors, and able to lift up to 50 lbs.",
      },
    },
    {
      id: 2,
      title: "Tyson's Warehouse Team Member",
      company: "Tyson Foods",
      location: "Springdale, AR",
      rating: "★ 3.8",
      salary: "$19 - $22 an hour",
      description:
        "Responsible for packaging and handling goods in a fast-paced warehouse environment.",
      details: {
        jobTitle: "Warehouse Worker",
        company: "Tyson Foods",
        rating: "★ 3.8",
        location: "Springdale, AR",
        postedTime: "2 days ago",
        qualifications: ["Warehouse", "Physical Stamina", "Team Player"],
        fullDescription:
          "Join our team at Tyson Foods as a warehouse worker. Duties include loading, unloading, and packaging food products in a temperature-controlled setting.",
      },
    },
  ];

  const [selectedJob, setSelectedJob] = useState(jobListings[0].details);
  const [searchLocation, setSearchLocation] = useState("");

  const filteredJobs = jobListings.filter((job) =>
    job.location.toLowerCase().includes(searchLocation.toLowerCase())
  );

  return (
    <div className="flex h-screen p-6 bg-gray-100">
      {/* Left Sidebar - Job Listings */}
      <div className="w-1/3 bg-white p-4 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4 text-gray-800">Job Listings</h2>

        {/* Search Box */}
        <input
          type="text"
          placeholder="Enter location..."
          className="w-full p-2 mb-4 border rounded focus:ring-2 focus:ring-blue-400"
          value={searchLocation}
          onChange={(e) => setSearchLocation(e.target.value)}
        />

        {/* Job List */}
        <div>
          {filteredJobs.length > 0 ? (
            filteredJobs.map((job) => (
              <div
                key={job.id}
                className="p-4 mb-3 border rounded cursor-pointer hover:bg-blue-100 transition-all duration-300"
                onClick={() => setSelectedJob(job.details)}
              >
                <h3 className="text-blue-700 font-bold">{job.title}</h3>
                <p className="text-gray-800 font-medium">
                  <span className="font-semibold">{job.company}</span> - {job.location}
                </p>
                <p className="text-gray-600">{job.salary}</p>
                <p className="text-gray-700 text-sm">{job.description}</p>
                <p className="text-pink-500 text-sm mt-2 cursor-pointer hover:underline">
                  Quick Apply
                </p>
              </div>
            ))
          ) : (
            <p className="text-gray-600">No jobs found for this location.</p>
          )}
        </div>
      </div>

      {/* Right Section - Job Details (More Appealing) */}
      <div className="w-2/3 ml-6 bg-white p-6 rounded-lg shadow-lg flex flex-col items-center">
        <div className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white w-full p-5 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold">{selectedJob.jobTitle}</h2>
          <p className="text-lg font-semibold">{selectedJob.company}</p>
          <div className="flex items-center mt-2">
            <FaStar className="text-yellow-400 mr-2" />
            <p>{selectedJob.rating}</p>
          </div>
        </div>

        <div className="w-full mt-5 p-5 bg-gray-50 rounded-lg shadow-md">
          {/* Location & Time */}
          <div className="flex items-center mb-3 text-gray-600">
            <FaMapMarkerAlt className="mr-2 text-red-500" />
            <p className="font-medium">{selectedJob.location}</p>
          </div>
          <div className="flex items-center text-gray-500 mb-3">
            <FaClock className="mr-2 text-blue-400" />
            <p>{selectedJob.postedTime}</p>
          </div>

          {/* Quick Apply Button */}
          <button className="bg-gradient-to-r from-pink-500 to-red-500 text-white py-2 px-6 rounded-lg shadow-md hover:scale-105 transition-transform duration-300">
            Quick Apply
          </button>

          {/* Job Qualifications */}
          <h3 className="mt-6 font-semibold text-lg text-gray-800">Qualifications:</h3>
          <ul className="list-disc list-inside text-gray-700">
            {selectedJob.qualifications.map((q, index) => (
              <li key={index} className="ml-2">{q}</li>
            ))}
          </ul>

          {/* Full Job Description */}
          <h3 className="mt-6 font-semibold text-lg text-gray-800">Job Description:</h3>
          <p className="text-gray-700">{selectedJob.fullDescription}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
