import { useState, useEffect, useCallback } from "react";
import "./App.css";

function JobList({ jobs, selectedJob, onSelectJob, location, onLocationChange }) {
  return (
    <div className="w-1/3 bg-white p-5 rounded-lg shadow-xl flex flex-col">
      <h2 className="text-2xl font-semibold mb-4 text-indigo-800 text-center">Job Listings</h2>

      <input
        type="text"
        className="w-full p-3 mb-4 border rounded-lg bg-indigo-50 text-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-400"
        value={location}
        onChange={onLocationChange}
        placeholder="Type a location..."
      />

      <div className="flex-1 overflow-y-auto">
        {jobs.length > 0 ? (
          jobs.map((job) => (
            <div
              key={job._id}
              className={`p-4 mb-3 border rounded-lg cursor-pointer transition-all duration-300 shadow-sm ${
                selectedJob?._id === job._id ? "bg-indigo-200" : "hover:bg-indigo-100"
              }`}
              onClick={() => onSelectJob(job)}
            >
              <h3 className="text-lg font-bold text-indigo-900">{job.title}</h3>
              <p className="text-pink-600 font-medium">
                {job.company} - <span className="text-gray-800">{job.location}</span>
              </p>
            </div>
          ))
        ) : (
          <p className="text-gray-500 text-center">No jobs available</p>
        )}
      </div>
    </div>
  );
}

function JobDetails({ job }) {
  if (!job) return null;

  const {
    title,
    company,
    location,
    employment_type,
    postedDateTime,
    source,
    experience,
    description,
  } = job;

  return (
    <div className="w-2/3 ml-6 bg-white p-8 rounded-lg shadow-xl overflow-auto border border-gray-200">
      <div className="max-w-2xl mx-auto">
        <h2 className="text-3xl font-bold text-indigo-900 text-center mb-12">{title}</h2>
        <div className="grid grid-cols-2 gap-4 mb-6">
          <p className="text-gray-600"><strong>Company:</strong> <span className="text-pink-600">{company || "Not specified"}</span></p>
          <p className="text-gray-600"><strong>Location:</strong> {location || "Not specified"}</p>
          <p className="text-gray-600"><strong>Employment Type:</strong> {employment_type || "Not specified"}</p>
          <p className="text-gray-600">
            <strong>Posted:</strong> {postedDateTime ? postedDateTime.split("T")[0] : "N/A"}
          </p>
          <p className="text-gray-600"><strong>Source:</strong> {source || "Unknown"}</p>
          <p className="text-gray-600"><strong>Experience Range:</strong> {experience || "Not specified"}</p>
        </div>
        <div className="mt-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4">Job Description:</h3>
          <p className="text-gray-700 leading-relaxed">
            {description || "No job description available."}
          </p>
        </div>
        <div className="mt-6 text-center">
          <button className="bg-indigo-500 text-white py-3 px-8 rounded-lg hover:bg-indigo-600 transition-all duration-300 shadow-md">
            Quick Apply
          </button>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [jobListings, setJobListings] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState("");

  useEffect(() => {
    if (!selectedLocation.trim()) return;

    fetch(`http://localhost:3000/jobs/search?location=${selectedLocation}`)
      .then((response) => response.json())
      .then((data) => {
        setJobListings(data);
        setSelectedJob(null);
      })
      .catch((error) => console.error("Error fetching job listings:", error));
  }, [selectedLocation]);

  const handleLocationChange = useCallback((e) => {
    setSelectedLocation(e.target.value);
  }, []);

  return (
    <div className="flex h-screen p-6 bg-gradient-to-r from-blue-50 to-indigo-100">
      <JobList
        jobs={jobListings}
        selectedJob={selectedJob}
        onSelectJob={setSelectedJob}
        location={selectedLocation}
        onLocationChange={handleLocationChange}
      />
      <JobDetails job={selectedJob} />
    </div>
  );
}

export default App;
