import React, { useState, useEffect } from "react";

const JobListings = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch job listings
  useEffect(() => {
    fetch(`http://localhost:5000/jobs?location=${searchQuery}`)
      .then((response) => response.json())
      .then((data) => setJobs(data))
      .catch((error) => console.error("Error fetching jobs:", error));
  }, [searchQuery]);

  // Fetch detailed job data
  const fetchJobDetails = (jobId) => {
    fetch(`http://localhost:5000/job-details/${jobId}`)
      .then((response) => response.json())
      .then((data) => setSelectedJob(data))
      .catch((error) => console.error("Error fetching job details:", error));
  };

  return (
    <div className="container">
      {/* Left Panel - Job Listings */}
      <div className="job-listings">
        <h2>Job Listings</h2>
        <input
          type="text"
          placeholder="Search location..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        {jobs.map((job) => (
          <div key={job.id} className="job-card" onClick={() => fetchJobDetails(job.id)}>
            <h3>{job.title}</h3>
            <p>{job.company} - {job.location}</p>
            <p>Salary: {job.salary || "Not disclosed"}</p>
          </div>
        ))}
      </div>

      {/* Right Panel - Job Details */}
      <div className="job-details">
        {selectedJob ? (
          <>
            <h2>{selectedJob.title}</h2>
            <p><strong>{selectedJob.company}</strong></p>
            <p>‚≠ê {selectedJob.rating || "Not rated"}</p>
            <p><strong>Location:</strong> {selectedJob.location}</p>
            <p><strong>Employment Type:</strong> {selectedJob.employmentType || "Not specified"}</p>
            <p><strong>Posted:</strong> {selectedJob.posted || "N/A"}</p>
            <p><strong>Source:</strong> {selectedJob.source || "Unknown"}</p>
            <p><strong>Experience Range:</strong> {selectedJob.experience || "Not specified"}</p>
            <button className="apply-button">Quick Apply</button>
            <h3>Job Description:</h3>
            <p>{selectedJob.description || "No description available."}</p>
          </>
        ) : (
          <p>Select a job to view details</p>
        )}
      </div>
    </div>
  );
};

export default JobListings;
