import { useState } from "react";
import "./App.css";

function App() {
  // Sample Job Data
  const jobs = [
    {
      id: 1,
      title: "Immediate Cleaning Position Available - Starting at $20.00/Hour",
      company: "Reliance Contractors",
      location: "Sandy, OR",
      rating: 3.6,
      salary: "$21 - $23 an hour",
      description:
        "Clean and sanitize work areas, break rooms, and other designated areas. Identify management of any damages or repairs needed. DO NOT SHOW UP AT THE FACILITY.",
      qualifications: ["Management", "Heavy Lifting", "Sanitation", "Entry Level"],
    },
    {
      id: 2,
      title: "Garbage Collector (Rider)",
      company: "Sam's Sanitation",
      location: "Marion, OH",
      rating: 4.0,
      salary: "$19 - $21 an hour",
      description:
        "Ride along with our team to collect and dispose of waste. Assist with lifting and sorting materials.",
      qualifications: ["Physical Work", "Outdoor Job", "Sanitation"],
    },
  ];

  // State to store selected job
  const [selectedJob, setSelectedJob] = useState(jobs[0]);

  return (
    <div className="flex min-h-screen bg-gray-100 p-5">
      {/* Left Section - Job Listings */}
      <div className="w-1/3 p-3 bg-white shadow-lg rounded-lg overflow-auto">
        <h2 className="text-xl font-semibold text-gray-700 mb-4">Job Listings</h2>
        {jobs.map((job) => (
          <div
            key={job.id}
            className={`p-3 border-b cursor-pointer ${
              selectedJob.id === job.id ? "bg-blue-100" : "hover:bg-gray-100"
            }`}
            onClick={() => setSelectedJob(job)}
          >
            <h3 className="text-blue-600 font-semibold">{job.title}</h3>
            <p className="text-gray-700">
              <span className="font-medium">{job.company}</span> - {job.location}
            </p>
            <p className="text-gray-600 text-sm mt-1">{job.salary}</p>
          </div>
        ))}
      </div>

      {/* Right Section - Job Details */}
      <div className="w-2/3 p-5 bg-white shadow-lg rounded-lg ml-5">
        <h2 className="text-2xl font-semibold text-gray-800">{selectedJob.title}</h2>
        <p className="text-gray-600">
          <span className="font-medium">{selectedJob.company}</span> - {selectedJob.location}
        </p>

        {/* Quick Apply Button */}
        <button className="mt-3 px-4 py-2 bg-pink-600 text-white rounded-lg shadow-md hover:bg-pink-700">
          Quick Apply
        </button>

        {/* Job Details */}
        <div className="mt-4">
          <h3 className="text-lg font-semibold">Job Details:</h3>
          <p className="text-gray-700">{selectedJob.salary}</p>
        </div>

        {/* Qualifications */}
        <div className="mt-4">
          <h3 className="text-lg font-semibold">Qualifications:</h3>
          <div className="flex flex-wrap gap-2 mt-1">
            {selectedJob.qualifications.map((q, index) => (
              <span key={index} className="bg-gray-200 px-3 py-1 rounded-full text-sm">
                {q}
              </span>
            ))}
          </div>
        </div>

        {/* Job Description */}
        <div className="mt-4">
          <h3 className="text-lg font-semibold">Full Job Description:</h3>
          <p className="text-gray-700 mt-2">{selectedJob.description}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
