import { useState, useEffect } from "react";

export default function JobSearch() {
  const [location, setLocation] = useState("");
  const [locations, setLocations] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/locations")
      .then((res) => res.json())
      .then((data) => setLocations(data));
  }, []);

  const handleSearch = () => {
    fetch(`http://localhost:3000/jobs/search?location=${location}`)
      .then((res) => res.json())
      .then((data) => setJobs(data));
  };

  return (
    <div className="flex h-screen p-4 bg-gray-100">
      {/* Left Side - Job Listings */}
      <div className="w-1/2 bg-white p-4 shadow-lg rounded-lg">
        <h2 className="text-xl font-semibold mb-4">Search Jobs</h2>
        <input
          type="text"
          className="w-full p-2 border rounded mb-2"
          placeholder="Type or select location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          list="location-options"
        />
        <datalist id="location-options">
          {locations.map((loc, index) => (
            <option key={index} value={loc} />
          ))}
        </datalist>
        <button
          className="w-full bg-blue-500 text-white p-2 rounded mt-2"
          onClick={handleSearch}
        >
          Search
        </button>

        {/* Job Listings */}
        <ul className="mt-4 space-y-3">
          {jobs.map((job) => (
            <li
              key={job._id}
              className="p-3 border rounded cursor-pointer hover:bg-gray-200"
              onClick={() => setSelectedJob(job)}
            >
              <h3 className="font-semibold">{job.title}</h3>
              <p className="text-gray-600">{job.company} - {job.location}</p>
            </li>
          ))}
        </ul>
      </div>

      {/* Right Side - Job Details */}
      <div className="w-1/2 bg-white p-4 shadow-lg rounded-lg ml-4">
        {selectedJob ? (
          <div>
            <h2 className="text-xl font-bold mb-2">{selectedJob.title}</h2>
            <p className="text-gray-600 mb-2">{selectedJob.company} - {selectedJob.location}</p>
            <p className="text-gray-800 mb-2">Experience: {selectedJob.experience}</p>
            <p className="text-gray-800">Employment Type: {selectedJob.employment_type}</p>
            <a
              href={selectedJob.job_link}
              className="mt-4 block text-center bg-green-500 text-white p-2 rounded"
              target="_blank"
              rel="noopener noreferrer"
            >
              Apply Now
            </a>
          </div>
        ) : (
          <p className="text-gray-500">Select a job to view details</p>
        )}
      </div>
    </div>
  );
}
