import React, { useState, useEffect } from "react";
import axios from "axios";

const JobSearch = ({ onSelectJob }) => {
  const [query, setQuery] = useState("");
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (query.length > 2) {
      setLoading(true);
      axios.get(`/jobs/search?location=${query}`)
        .then(response => {
          setJobs(response.data);
          setLoading(false);
        })
        .catch(error => console.error("Error fetching jobs", error));
    } else {
      setJobs([]);
    }
  }, [query]);

  return (
    <div className="w-1/2 p-4 border-r">
      <input
        type="text"
        className="w-full p-2 border rounded"
        placeholder="Search by location..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      {loading && <p>Loading...</p>}
      <ul className="mt-4 border rounded">
        {jobs.map((job) => (
          <li 
            key={job.jobID} 
            className="p-2 border-b cursor-pointer hover:bg-gray-200"
            onClick={() => onSelectJob(job.jobID)}
          >
            {job.title} - {job.company} ({job.location})
          </li>
        ))}
      </ul>
    </div>
  );
};

const JobDetails = ({ jobID }) => {
  const [job, setJob] = useState(null);

  useEffect(() => {
    if (jobID) {
      axios.get(`/job/details?jobID=${jobID}`)
        .then(response => setJob(response.data))
        .catch(error => console.error("Error fetching job details", error));
    }
  }, [jobID]);

  if (!job) return <div className="w-1/2 p-4">Select a job to see details</div>;

  return (
    <div className="w-1/2 p-4">
      <h2 className="text-xl font-bold">{job.title}</h2>
      <p><strong>Company:</strong> {job.company}</p>
      <p><strong>Location:</strong> {job.location}</p>
      <p><strong>Type:</strong> {job.employment_type}</p>
      <p><strong>Experience:</strong> {job.experience} years</p>
      <a href={job.job_link} className="text-blue-500" target="_blank" rel="noopener noreferrer">Apply Here</a>
    </div>
  );
};

const JobPortal = () => {
  const [selectedJobID, setSelectedJobID] = useState(null);

  return (
    <div className="flex h-screen">
      <JobSearch onSelectJob={setSelectedJobID} />
      <JobDetails jobID={selectedJobID} />
    </div>
  );
};

export default JobPortal;
