import { useState, useEffect } from "react";

export default function JobSearch() {
  const [location, setLocation] = useState("");
  const [locations, setLocations] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/locations")
      .then((res) => res.json())
      .then((data) => setLocations(data))
      .catch((err) => console.error("Error fetching locations:", err));
  }, []);

  const handleSearch = () => {
    fetch(`http://localhost:3000/jobs/search?location=${location}`)
      .then((res) => res.json())
      .then((data) => setJobs(data))
      .catch((err) => console.error("Error fetching jobs:", err));
  };

  const handleJobClick = (job) => {
    fetch(`http://localhost:3000/job/details?jobID=${job._id}`)
      .then((res) => res.json())
      .then((data) => setSelectedJob(data))
      .catch((err) => console.error("Error fetching job details:", err));
  };

  return (
    <div className="flex h-screen p-4">
      {/* Left side: Job Search & Listing */}
      <div className="w-1/2 p-4 border-r border-gray-300">
        <h2 className="text-2xl font-semibold mb-4">Search Jobs</h2>
        <select
          className="w-full p-2 border rounded mb-4"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        >
          <option value="">Select Location</option>
          {locations.map((loc, index) => (
            <option key={index} value={loc}>{loc}</option>
          ))}
        </select>
        <button
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          onClick={handleSearch}
        >
          Search
        </button>

        <div className="mt-4">
          {jobs.map((job) => (
            <div
              key={job._id}
              className="p-4 border rounded mb-2 cursor-pointer hover:bg-gray-100"
              onClick={() => handleJobClick(job)}
            >
              <h3 className="text-lg font-semibold">{job.title}</h3>
              <p className="text-gray-600">{job.company}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Right side: Job Details */}
      <div className="w-1/2 p-4">
        {selectedJob ? (
          <div>
            <h2 className="text-2xl font-semibold">{selectedJob.title}</h2>
            <p className="text-gray-600 mb-2">{selectedJob.company} - {selectedJob.location}</p>
            <p className="text-gray-800 mb-4">Experience Required: {selectedJob.experience}</p>
            <a
              href={selectedJob.job_link}
              target="_blank"
              rel="noopener noreferrer"
              className="bg-green-500 text-white p-2 rounded hover:bg-green-600"
            >
              Apply Now
            </a>
          </div>
        ) : (
          <p className="text-gray-500">Select a job to see details</p>
        )}
      </div>
    </div>
  );
}
