import { useState, useEffect } from "react";

export default function JobSearch() {
  const [location, setLocation] = useState("");
  const [locations, setLocations] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/locations")
      .then((res) => res.json())
      .then((data) => setLocations([...new Set(data)]));
  }, []);

  const handleSearch = () => {
    fetch(`http://localhost:3000/jobs/search?location=${location}`)
      .then((res) => res.json())
      .then((data) => {
        const uniqueJobs = Array.from(new Map(data.map(job => [job._id, job])).values());
        setJobs(uniqueJobs);
      });
  };

  return (
    <div className="flex h-screen p-6 bg-gradient-to-r from-blue-500 to-purple-600 text-white">
      {/* Left Side - Job Listings */}
      <div className="w-1/2 bg-white p-6 shadow-lg rounded-lg text-black">
        <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">Job Listing</h2>
        <input
          type="text"
          className="w-full p-3 border rounded-lg mb-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Type or select location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          list="location-options"
        />
        <datalist id="location-options">
          {locations.map((loc, index) => (
            <option key={index} value={loc} />
          ))}
        </datalist>
        <button
          className="w-full bg-blue-600 hover:bg-blue-700 text-white p-3 rounded-lg font-semibold mt-2 transition duration-300"
          onClick={handleSearch}
        >
          Search Jobs
        </button>

        {/* Job Listings */}
        <ul className="mt-6 space-y-4 max-h-80 overflow-y-auto">
          {jobs.map((job) => (
            <li
              key={job._id}
              className="p-4 border rounded-lg bg-gray-100 hover:bg-gray-200 cursor-pointer transition duration-300"
              onClick={() => setSelectedJob(job)}
            >
              <h3 className="font-semibold text-gray-800">{job.title}</h3>
              <p className="text-gray-600">{job.company} - {job.location}</p>
            </li>
          ))}
        </ul>
      </div>

      {/* Right Side - Job Details */}
      <div className="w-1/2 bg-white p-6 shadow-lg rounded-lg ml-6 text-black flex items-center justify-center">
        {selectedJob ? (
          <div>
            <h2 className="text-2xl font-bold mb-3 text-gray-800">{selectedJob.title}</h2>
            <p className="text-gray-600 mb-2">{selectedJob.company} - {selectedJob.location}</p>
            <p className="text-gray-700 mb-2">Experience: {selectedJob.experience}</p>
            <p className="text-gray-700">Employment Type: {selectedJob.employment_type}</p>
            <a
              href={selectedJob.job_link}
              className="mt-6 block text-center bg-green-500 hover:bg-green-600 text-white p-3 rounded-lg font-semibold transition duration-300"
              target="_blank"
              rel="noopener noreferrer"
            >
              Apply Now
            </a>
          </div>
        ) : (
          <p className="text-gray-500 text-lg">Select a job to view details</p>
        )}
      </div>
    </div>
  );
}
